import React from "react";
import uuid from "uuid/v1";

import NoteList from "./NoteList";
import NewNote from "./NewNote";
import AppBar from "./AppBar";


class App  extends React.Component{
    state = {
             notes: [
                 {id: 1, text: "Teste 1"},
                 {id: 2, text: "Teste 2"},
                 {id: 3, text: "Teste 3"},
                 {id: 4, text: "Teste 4"},
                 {id: 5, text: "Teste 5"},


             ]   
         }
     handleAddNote = text =>{
         this.setState(prevState => ({
             notes: prevState.notes.concat({ id: uuid(), text})
         }));
     }

     handleMove = (direction, index) => {
         this.setState(prevState => {
             // clona o array para nao perder o stado original
             const newNotes = prevState.notes.slice();
             // remove a nota do indice informado
             const removedNota = newNotes.splice(index, 1) [0];
             // colocar o item removidona posição nova
             // se o movimento for para cima colocamos a nota na posição anterior
             if(direction === "up"){
                 newNotes.splice(index - 1, 0, removedNota);
             } else {
                 newNotes.splice(index + 1, 0, removedNota);
             }


             // pegar o item da nota removida
             return{
                 notes: newNotes
             };
         });
     };

     handleDelete = id => {
         this.setState(prevState => {
             // clona o array para nao perder o stado original
             const newNotes = prevState.notes.slice();
             const index = newNotes.findIndex(note => note.id === id);
             // remove a nota do indice informado
             newNotes.splice(index, 1);
             // colocar o item removidona posição nova
             // se o movimento for para cima colocamos a nota na posição anterior
             // pegar o item da nota removida
             return{
                 notes: newNotes
             };
         });
     };

     handleEdit = (id, text) => {
         this.setState(prevState => {
           const newNotes = prevState.notes.slice();
           const index = newNotes.findIndex(note => note.id === id);
           newNotes[index].text = text;

           return {
             notes: newNotes
           };
         });
       };

     render(){
         return (
             <div>
                <AppBar/>
                <div className="container">
                    <NewNote onAddNote={this.handleAddNote} />
                    <NoteList 
                    notes={this.state.notes} 
                    onMove={this.handleMove} 
                    onDelete={this.handleDelete}
                    onEdit={this.handleEdit}
                    />
                </div>
            </div>
         );
     }
 }

export default App;
